package javaassignment.packages;

public class EvenOddBw0to100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
	for(int i=0;i<=100;i++)
	{
		if(i%2==0)
		{
			System.out.println(i+" Number is Even");
		}
		else
		{
			System.out.println(i+" Number is odd");
		}
	}
	}

}
